  const params = useParams();
  const { name } = params;
  const [Product, setProducts] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      const result = await axios.get(`http://localhost:3001/api/products/${_id}`);
      setProducts(result.data);
    };
    fetchData();
  }, [name]);
  return (
    <div>
      {Product.name}
      {Product.price}
  
    </div>
  );
}
export default ProductScreen;
